/*******************************************获取I2C地址以及引脚定义*******************************************/
uint8_t u8x8_byte_i2c(u8x8_t *u8x8, uint8_t msg, uint8_t arg_int, void *arg_ptr)
{
    static uint8_t buffer[32];		/* u8g2/u8x8 will never send more than 32 bytes between START_TRANSFER and END_TRANSFER */
    static uint8_t buf_idx;
    uint8_t *data;

    switch(msg)
    {
        case U8X8_MSG_BYTE_SEND:
            data = (uint8_t *)arg_ptr;
            while( arg_int > 0 )
            {
                buffer[buf_idx++] = *data;
                data++;
                arg_int--;
            }
            break;
        case U8X8_MSG_BYTE_START_TRANSFER:
            buf_idx = 0;
            break;
        case U8X8_MSG_BYTE_END_TRANSFER:
            HAL_I2C_Master_Transmit(&hi2c1, u8x8_GetI2CAddress(u8x8), buffer, buf_idx, 500);
            break;
        default:
            return 0;
    }
    return 1;
}
/*******************************************获取I2C地址以及引脚定义*******************************************/

/**********************************************OLED微秒延时函数**********************************************/
uint8_t u8x8_delay(u8x8_t *u8x8, uint8_t msg, uint8_t arg_int, void *arg_ptr)
{
    switch (msg)
    {
        case U8X8_MSG_DELAY_MILLI:
            HAL_Delay(arg_int);

            break;
            //Function which delays 10us
        case U8X8_MSG_DELAY_10MICRO:
            for (uint16_t n = 0; n < 320; n++)
            {
                __NOP();
            }

            break;
            //Function which delays 100ns
        case U8X8_MSG_DELAY_100NANO:
            __NOP();
            break;
        default:
            return 0;
    }
    return 1;
}
/**********************************************OLED微秒延时函数**********************************************/

/************************************************OLED初始化************************************************/
void oled_Init(u8g2_t *u8g2) {
    u8g2_Setup_sh1107_i2c_128x80_f(u8g2, U8G2_R3, u8x8_byte_i2c, u8x8_delay);
    u8g2_InitDisplay(u8g2); // send init sequence to the display, display is in sleep mode after this,
    u8g2_ClearDisplay(u8g2);   //清空屏幕
    u8g2_SetPowerSave(u8g2, 0); // wake up display
}
/************************************************OLED初始化************************************************/
#define SEND_BUFFER_DISPLAY_MS(u8g2, ms)\
  do {\
    u8g2_SendBuffer(u8g2); \
    HAL_Delay(ms);\
  }while(0);
/************************************************OLED初始化************************************************/


